SELECT IFNULL(SUM(o.brokerage_price), 0) money from (SELECT brokerage_price,from_member_id, to_member_id from t_union_opportunity
WHERE is_accept = 2 AND del_status = 0 AND from_member_id in

      #{member.id}
  )
AND NOT EXISTS (SELECT id from t_union_brokerage_income i WHERE t_union_opportunity.id = i.opportunity_id)) o
LEFT JOIN t_union_member m on o.to_member_id = m.id WHERE m.del_status = 0 and m.`status` in(2,3)

SELECT IFNULL(SUM(o.brokerage_price), 0) money from (SELECT brokerage_price,from_member_id, to_member_id from t_union_opportunity WHERE is_accept = 2 AND del_status = 0 AND to_member_id in
#{member.id}
and NOT EXISTS (SELECT * from t_union_brokerage_income i WHERE t_union_opportunity.id = i.opportunity_id)) o LEFT JOIN t_union_member m on o.from_member_id = m.id WHERE m.del_status = 0 and m.`status` in(2,3)

SELECT o.id, o.brokerage_price money, o.modifytime time, m2.enterprise_name name, um.`name` unionName from t_union_opportunity o LEFT JOIN (SELECT id,union_id from t_union_member WHERE id in
#{member.id}
) m on o.to_member_id = m.id LEFT JOIN t_union_main um on um.id = m.union_id LEFT JOIN t_union_member m2 on m2.id = o.from_member_id WHERE o.del_status = 0 and o.is_accept = 2 and o.to_member_id = m.id AND m2.del_status = 0 and m2.`status` in (2,3) NOT EXISTS (SELECT * from t_union_brokerage_income i WHERE o.id = i.opportunity_id)

SELECT o.brokerage_price as money, o.modifytime as time, m.enterprise_name as name, um.name as unionName from (SELECT brokerage_price,from_member_id, to_member_id, modifytime from t_union_opportunity WHERE is_accept = 2 AND del_status = 0 AND to_member_id in
#{member.id}
and EXISTS (SELECT id from t_union_brokerage_income i WHERE t_union_opportunity.id = i.opportunity_id)) o LEFT JOIN t_union_member m on o.from_member_id = m.id LEFT JOIN t_union_main um on um.id = m.union_id WHERE m.del_status = 0 and m.`status` in (2,3)

