SELECT m.enterprise_name as name, i.createtime as time, um.name as unionName, i.money from (SELECT id, member_id from t_union_card WHERE member_id in (SELECT id from t_union_member WHERE union_id in
#{member.unionId}
) and del_status = 0 and status in (2,3))) c LEFT JOIN (SELECT id, card_id, createtime, money from t_union_brokerage_income WHERE bus_id = #{busId} and del_status = 0 ) i on c.id = i.card_id LEFT JOIN t_union_member m on m.id = c.member_id LEFT JOIN t_union_main um on um.id = m.union_id WHERE i.id is NOT NULL

